(()=>{document.addEventListener("DOMContentLoaded",function(){console.log("Theme toggle disabled"),o()});function o(){document.querySelectorAll("img[alt]").forEach(function(e){let t=e.getAttribute("alt");if(!t||t.trim()===""||e.parentElement.classList.contains("image-container")||e.closest("figure")||/^(icon|logo|decoration|spacer)$/i.test(t.trim()))return;let n=document.createElement("div");n.className="image-container";let a=document.createElement("div");a.className="image-caption",a.textContent=t,e.parentNode.insertBefore(n,e),n.appendChild(e),n.appendChild(a)})}})();
